import {useSpring, animated} from 'react-spring'

export default () => {
  const textSpring = useSpring({ text: 'hola como estas', from: { text: '' } })
  return (
    <div>
      <div className='bg-earth bg-no-repeat bg-fixed min-h-screen flex justify-center items-center'>
        <div className='box relative bg-black bg-opacity-90 rounded-lg p-6 max-w-lg'>
          <img className='box-img object-contain float-left w-20 mr-4 mt-2 rounded-lg' src='/assets/static/iam.jpg' alt='' />
          <animated.p className='box-text text-lg text-white'>{textSpring}</animated.p>
        </div>
      </div>
      <style jsx>{`
        .bg-earth::before {
          content: "";
          position: absolute;
          top: 0;
          left: 0;
          width: 100%; height: 100%;
          background-color: black;
          background-image: url("/assets/static/earth.jpg");
          /*filter: grayscale(50%);
          filter: sepia(60%);*/
        }
        .box {
          box-shadow: 0 0 5px black,0 0 10px black,0 0 15px black,0 0 20px red,0 0 25px red;
        }
        .box-img {
          filter: hue-rotate(90deg);
        }
        .bg-opacity-90{
          --bg-opacity: 0.90;
        };
        .box-text {
          /*text-shadow: 0 0 5px #ffffff, 0 0 10px red, 0 0 15px red;
          box-shadow: 0 0 5px #fff,0 0 10px #fff,0 0 15px #fff,0 0 20px #076eb3,0 0 25px #076eb3;*/
        }
      `}</style>
    </div>
  )
}
