export function typeWriter(txt, parentId) {
  let i = 0;
  const speed = 50;
  console.log(txt)
  if (i < txt.length) {
    document.getElementById(parentId).innerText += txt.charAt(i);
    i++;
    setTimeout(typeWriter, speed);
  }
}
